export const GRIDDLE_SORT = 'GRIDDLE_SORT';
export const GRIDDLE_SORTED = 'GRIDDLE_SORTED';
export const GRIDDLE_SORT_ADDITIONAL = 'GRIDDLE_SORT_ADDITIONAL';
export const GRIDDLE_SORTED_ADDITIONAL = 'GRIDDLE_SORTED_ADDITIONAL';
export const GRIDDLE_LOAD_DATA = 'GRIDDLE_LOAD_DATA';
export const GRIDDLE_LOADED_DATA = 'GRIDDLE_LOADED_DATA';
export const GRIDDLE_NEXT_PAGE = 'GRIDDLE_NEXT_PAGE';
export const GRIDDLE_PREVIOUS_PAGE = 'GRIDDLE_PREVIOUS_PAGE';
