} from '../data-helpers';

import { getBasicState } from './stateUtils';

const withoutMetadata = (item) => {
  delete item["__metadata"];
  return item;
}

describe('data helpers', () => {
  describe('getVisibleData', () => {
    it('gets visible data', () => {
      const state = getBasicState();
      const results = getVisibleData(state);
      expect(results.toJSON().map(withoutMetadata)).toEqual([{two: 'two', one: 'one'}, {two: 'four', one: 'three'}]);
    });

    it('only gets data for visible columns', () => {
      const columnProperties = getBasicState().getIn(['renderProperties', 'columnProperties']).filterNot(col => col.get('id') === 'one');
      const state = getBasicState().setIn(['renderProperties', 'columnProperties'], columnProperties);
      const results = getVisibleData(state);

      expect(results.toJSON().map(withoutMetadata)).toEqual([{two: 'two'}, {two: 'four'}]);
    })

    it('gets the index', () => {
      const columnProperties = getBasicState().getIn(['renderProperties', 'columnProperties']).filterNot(col => col.get('id') === 'one');
      const state = getBasicState().setIn(['renderProperties', 'columnProperties'], columnProperties);
      const results = getVisibleData(state);

      expect(results.toJSON().every((r, i) => r.__metadata.index === i)).toEqual(true)
    });
  });

  describe('getVisibleDataColumns', () => {
    it('gets data for the given columns', () => {
      const state = getBasicState();
      const results = getVisibleDataColumns(state.get('data'), ['one', 'two']);

      expect(results.toJSON().map(withoutMetadata)).toEqual([{two: 'two', one: 'one'}, {two: 'four', one: 'three'}]);
    });

    it('sorts the columns', () => {
      const state = getBasicState();
      const sorted = getVisibleDataColumns(state.get('data'), ['two', 'one']);

      expect(Object.keys(sorted.toJSON()[0])).toEqual(['two', 'one', '__metadata']);
    });

    //test for the columns that are made up from thin air
    it('gets null for magic column', () => {
      const state = getBasicState();
      const results = getVisibleDataColumns(state.get('data'), ['one', 'onepointfive', 'two']);

      expect(results.toJSON().map(withoutMetadata)).toEqual([{two: 'two', onepointfive: null,  one: 'one'}, {two: 'four', onepointfive: null,  one: 'three'}]);
    });

    it('gets correct order for magic column', () => {
      const state = getBasicState();
      const results = getVisibleDataColumns(state.get('data'), ['one', 'onepointfive', 'two']);

      expect(Object.keys(results.toJSON()[0])).toEqual(['one', 'onepointfive', 'two', '__metadata']);
    });
  })

  describe('updateVisibleData', () => {
    it('sets visible data', () => {
      const state = getBasicState();
      const nextState = updateVisibleData(state);

      expect(state.get('data').toJSON().map(withoutMetadata)).toEqual(nextState.get('visibleData').toJSON().map(withoutMetadata));
    });
  });

  describe('getState', () => {
    it('gets state', () => {
      const state = getBasicState();

      expect(getState(state)).toEqual(state);
    });
  });

  describe('getPageProperties', () => {
    it('gets page properties', () => {
      const state = getBasicState();

      expect(getPageProperties(state)).toEqual(state.get('pageProperties'));
    })
  });

  describe('addKeyToRows', () => {
    it('adds a key to all rows', () => {
      const state = getBasicState();
      const newData = addKeyToRows(state.get('data'));

      expect(newData.get(0).get('griddleKey')).toBeDefined();
    });
  });

  describe('getPageCount', () => {
    it('gets the correct number of pages', () => {
      expect(getPageCount(50, 5)).toEqual(10);
      expect(getPageCount(50, 6)).toEqual(9);
    });
  });

  describe('getColumnTitles', () => {
    it('gets the correct titles', () => {
      const state = getBasicState();
      const titles = getColumnTitles(state);

      expect(titles).toEqual({
        one: { one: 'One' },
        two: { two: 'Two' }
      });
    });
  });

  describe('getColumnProperties', () => {
    it('gets column properties', () => {
      const state = getBasicState();
      const properties = getColumnProperties(state);

      expect(properties).toEqual(state.getIn(['renderProperties', 'columnProperties']).toJSON())
    });

    it('returns empty object on state without columnProperties', () => {
      const state = Immutable.fromJS({
        renderProperties: {}
      });
      const properties = getColumnProperties(state);

      expect(properties).toEqual({});
    });
  });

  describe('getAllPossibleColumns', () => {
    it('gets as Seq containing keys from data', () => {
      const state = getBasicState();
      const possibleColumns = getAllPossibleColumns(state);

      expect(possibleColumns.toJSON()).toEqual(['one', 'two'])
    });
  });

  describe('getDataColumns', () => {
    it('gets the columns arranged by order', () => {
      const state = getBasicState();
      const dataColumns = getDataColumns(state);

      expect(dataColumns).toEqual(['two', 'one']);
    });

    it('gets only the visible columns', () => {
      const columnProperties = getBasicState().getIn(['renderProperties', 'columnProperties']).filterNot(col => col.get('id') === 'one');
      const state = getBasicState().setIn(['renderProperties', 'columnProperties'], columnProperties);
      const dataColumns = getDataColumns(state);

      expect(dataColumns).toEqual(['two']);
    });

    it('gets all columns if visible columns is empty', () => {
      const columnProperties = getBasicState().getIn(['renderProperties', 'columnProperties']).filterNot(col => (col.get('id') === 'one' || col.get('id') === 'two'));
      const state = getBasicState().setIn(['renderProperties', 'columnProperties'], columnProperties);
      const dataColumns = getDataColumns(state);

      //this needs to be one, two because it's not checking the order property
      expect(dataColumns).toEqual(['one', 'two']);
    })
  });
});

import Immutable from 'immutable';

import {
  getVisibleData,
  hasNext,
  hasPrevious,
  getDataSet,
  filterData,
  filterDataByColumns,
  getSortedData,
  updateSortColumns,
  sortDataByColumns,
  getPage
} from '../local-helpers';

//TODO: Make this a method in a utils because it's shared with dataHelperTests
const withoutMetadata = (item) => {
  delete item["__metadata"];
  return item;
}

import { getBasicState } from './stateUtils';

describe('localHelpers', () => {
  it('gets visible data', () => {
    const state = getBasicState();
    const visibleData = getVisibleData(state);

    expect(visibleData.size).toEqual(state.getIn(['pageProperties', 'pageSize']));
    expect(visibleData.toJSON().map(withoutMetadata)).toEqual([{two: 'two', one: 'one'}]);
  });

  describe('hasNext', () => {
    it('returns true when on first page and there are more results', () => {
      //this is currently one page and one result per page and there are two items
      const state = getBasicState();
      const hasMore  = hasNext(state);

      expect(hasMore).toEqual(true);
    });

    it('returns false when on the last page', () => {
      const state = getBasicState().setIn(['pageProperties', 'currentPage'], 2);
      const hasMore = hasNext(state);

      expect(hasMore).toEqual(false);
    });
  });

  describe('hasPrevious', () => {
    it('returns false when on first page', () => {
      const state = getBasicState();
      const localHasPrevious  = hasPrevious(state);

      expect(localHasPrevious).toEqual(false);
    });

    it('returns false when on the last page', () => {
      const state = getBasicState().setIn(['pageProperties', 'currentPage'], 2);
      const localHasPrevious  = hasPrevious(state);

      expect(localHasPrevious).toEqual(true);
    });
  });

  describe('getDataSet', () => {
    it('returns state if no filter present', () => {
      const state = getBasicState();
      const dataState = getDataSet(state);

      expect(state.get('data')).toEqual(dataState);
    });

    //TODO: Add test to make sure it calls filter method when there is a filter present
    it('returns filtered data if columnFilters present', () => {
      const state = getBasicState()
        .set('columnFilters', Immutable.List([
          {column: 'three', filter: 'r'}
        ]))
        .set('data', Immutable.fromJS([
          {one: 'one', two: 'two', three: 'three'},
          {one: 'four', two: 'six', three: 'seven'},
          {one: 'ichi', two: 'ni', three: 'san'},
          {one: 'shi', two: 'go', three: 'roku'}
        ]));

      const dataState = getDataSet(state);

      expect(dataState.toJSON()).toEqual([
        {one: 'one', two: 'two', three: 'three'},
        {one: 'shi', two: 'go', three: 'roku'}
      ])
    })
  });

  describe('filterData', () => {
    it('returns filtered data', () => {
      const state = getBasicState();
      const filteredData = filterData(state.get('data'), 'four');

      expect(filteredData.size).toEqual(1);
      expect(filteredData.toJSON()).toEqual([{one: 'three', two: 'four'}]);
    });

    it('returns the whole dataset when no filter is present', () => {
      const state = getBasicState();
      const filteredData = filterData(state.get('data'), '');

      expect(filteredData.size).toEqual(2);
      expect(filteredData.toJSON()).toEqual(state.get('data').toJSON());
    });

    //TODO: Break this test down a bit
    it('filters by column', () => {
      const state = getBasicState()
        .set('data', Immutable.fromJS([
          {one: 'one', two: 'two', three: 'three'},
          {one: 'four', two: 'six', three: 'seven'},
          {one: 'ichi', two: 'ni', three: 'san'},
          {one: 'shi', two: 'go', three: 'roku'}
        ]));

      const filteredData = filterDataByColumns(state.get('data'), Immutable.List([
        {column: 'three', filter: 'r'},
      ]));

      expect(filteredData.toJSON()).toEqual([
        {one: 'one', two: 'two', three: 'three'},
        {one: 'shi', two: 'go', three: 'roku'}
      ]);

      const filteredData2 = filterDataByColumns(state.get('data'), Immutable.List([
        {column: 'three', filter: 'r'},
        {column: 'one', filter: 's'}
      ]))

      expect(filteredData2.toJSON()).toEqual([
        {one: 'shi', two: 'go', three: 'roku'}
      ]);

      const filteredData3 = filterDataByColumns(state.get('data'), Immutable.fromJS([
        {column: 'three', filter: 'r'},
        {column: 'one', filter: 's'},
        {column: 'two', filter: 'w'}
      ]))

      expect(filteredData3.toJSON()).toEqual([]);
    })
  });

  describe('getSortedData', () => {
    it('sorts the data', () => {
      const state = getBasicState();
      const updatedState = updateSortColumns(state, ['two'], true);
      const sortedData = sortDataByColumns(updatedState);

      expect(sortedData.get('data').toJSON()).toEqual([{one: 'three', two: 'four'}, {one: 'one', two: 'two'}]);
    });

    it('sorts in reverse order when sortAscending = false', () => {
      const state = getBasicState();
      const updatedState = updateSortColumns(state, ['two'], false);
      const sortedData = sortDataByColumns(updatedState);

      expect(sortedData.get('data').toJSON()).toEqual([{one: 'one', two: 'two'}, {one: 'three', two: 'four'}]);
    });

    it('sorts by date', () => {
      const state = getBasicState()
        .set('data', Immutable.fromJS([
          {one: '1/1/1900', two: '1/5/2016'},
          {one: '7/20/1982', two: '8/21/2015'}]))
        .setIn(
          ['renderProperties', 'columnProperties', 'two'],
          new Immutable.Map({ sortType: 'date', id: 'two', displayName: 'Two'}))

      const updatedState = updateSortColumns(state, ['two']);

      const sortedData = sortDataByColumns(updatedState);

      expect(sortedData.get('data').toJSON())
        .toEqual([{one: '7/20/1982', two: '8/21/2015'}, {one: '1/1/1900', two: '1/5/2016'}]);
    });

    it('sorts by date descending', () => {
      const state = getBasicState()
        .set('data', Immutable.fromJS([
          {one: '1/1/1900', two: '1/5/2016'},
          {one: '7/20/1982', two: '8/21/2015'}]))
        .setIn(
          ['renderProperties', 'columnProperties', 'two'],
          new Immutable.Map({ sortType: 'date', id: 'two', displayName: 'Two'}))

      const updatedState = updateSortColumns(state, ['two'], false);

      const sortedData = sortDataByColumns(updatedState);

      expect(sortedData.get('data').toJSON())
        .toEqual([{one: '1/1/1900', two: '1/5/2016'}, {one: '7/20/1982', two: '8/21/2015'}]);
    });

  });

  describe('updateSortColumns', () => {
    it('sorts the data', () => {
      const state = getBasicState();
      const updatedState = updateSortColumns(state, ['two']);
      const sortedByColumns = sortDataByColumns(updatedState);

      expect(sortedByColumns.get('data').toJSON()).toEqual([{one: 'three', two: 'four'}, {one: 'one', two: 'two'}]);
    });

    it('sorts in reverse order when sortAscending = false', () => {
      const state = getBasicState();
      const updatedState = updateSortColumns(state, ['two'], false);
      const sortedData = sortDataByColumns(updatedState);

      expect(sortedData.get('data').toJSON()).toEqual([{one: 'one', two: 'two'}, {one: 'three', two: 'four'}]);
    });
  });

  describe('getPage', () => {
    it('gets the specified page', () => {
      const state = getBasicState();
      const firstPage = getPage(state, 1);
      const secondPage = getPage(state, 2);
      const invalidPage = getPage(state, 5);

      expect(firstPage.getIn(['pageProperties', 'currentPage'])).toEqual(1);
      expect(firstPage.getIn(['pageProperties', 'maxPage'])).toEqual(2);

      expect(secondPage.getIn(['pageProperties', 'currentPage'])).toEqual(2);
      expect(secondPage.getIn(['pageProperties', 'maxPage'])).toEqual(2);

      expect(invalidPage.getIn(['pageProperties', 'currentPage'])).toEqual(0);
      expect(invalidPage.getIn(['pageProperties', 'maxPage'])).toEqual(2);
    });
  })
});

